// create a array of movies objects
var myMovies = [
  {
    name: "Howl's Moving Castle",
    year: "2005",
    director: "Hayao Miyazaki",
    picture: "https://m.media-amazon.com/images/M/MV5BNmM4YTFmMmItMGE3Yy00MmRkLTlmZGEtMzZlOTQzYjk3MzA2XkEyXkFqcGdeQXVyMTMxODk2OTU@._V1_.jpg",
  },
  {
    name: "Rocky Balboa",
    year: "2006",
    director: "Sylvester Stallone",
    picture: "https://upload.wikimedia.org/wikipedia/en/d/db/Rocky_Balboa_%282006%29_theatrical_poster.jpg",
  },
  {
    name: "The Wind Rises",
    year: "2014",
    director: "Hayao Miyazaki",
    picture: "https://m.media-amazon.com/images/M/MV5BMTU4NDg0MzkzNV5BMl5BanBnXkFtZTgwODA3Mzc1MDE@._V1_.jpg",
  },
  {
    name: "Shaun of the Dead",
    year: "2004",
    director: "Edgar Wright",
    picture: "data:image/jpeg;base64,",
  },
  {
    name: "Taxi Driver",
    year: "1976",
    director: "Martin Scrosese",
    picture: "data:image/jpeg;base64,",
  },
];

// create array of <li> items 
var list_items = myMovies.map((movie) => {
  var item = `<li>Name: ${movie["name"]}<br>
                  Year: ${movie["year"]}<br>
                  Name: ${movie["director"]}<br>
                  <img src="${movie["picture"]}"/><br>
              </li><br>`;
  return item;
});

// append all the <li> items in a string variable called 'content'
var content = "";
list_items.forEach((item) => {
  content += item;
});

// get the ol element from html and set the content
document.getElementById("mylist").innerHTML = content;